mapper.select:
SELECT 
FROM study_log
WHERE user_id = #{userId}
AND start_time BETWEEN #{startDate} AND #{endDate}
ORDER BY start_time DESC

SELECT COALESCE(SUM(focus_duration), 0)
FROM study_log
WHERE user_id = #{userId}

SELECT 
FROM study_log
WHERE user_id = #{userId}
AND efficiency_score >= #{minScore}
ORDER BY efficiency_score DESC, start_time DESC
LIMIT #{limit}
